import React, { Component } from 'react';
//import logo from './logo.svg';
import './App.css';
import Person from './Person/Person'


class App extends Component {
  state = {
    persons : [
      { name : 'Rahul', place : 'Panvel' }, 
      { name : 'Hemchandra', place : 'Banglore' },
      { name : 'Shrisha', place : 'Mulund' }
    ]
  }

  switchNameHandler = () => {
    console.log("The Button was clicked");
    this.setState({
      persons : [
        { name : 'Avinash', place : 'Panvel' }, 
        { name : 'Abc', place : 'Banglore' },
        { name : 'Xyz', place : 'Delhi' }
      ]})
  }

  nameChangedHandler = (props) => {
    console.log("printing events------->>>", props)
 this.setState({
      persons : [
        { name : 'Avinash', place : 'Panvel' }, 
        { name : 'Manish', place : 'Banglore' },
        { name : props.target.value, place : 'Delhi' }
      ]})
  }
  render() {
    const style = {
      backgroundColor : 'white',
      font: 'inherit',
      border: '1px solid blue',
      padding : '8px', 
      boxShadow: '0 2px 3px rgb(73, 10, 219)', 
      cursor: 'pointer'
    }
  return (
    <div className="App">
      <h1>Hi, I am a React App</h1>
      <p>This is really Working</p>
      <button 
      style={style}
      onClick={this.switchNameHandler} >Switch Button</button>
      <Person name={this.state.persons[0].name} place={this.state.persons[0].place}/>
      <Person name={this.state.persons[1].name} place={this.state.persons[1].place}/>
      <Person name={this.state.persons[2].name} place={this.state.persons[2].place}
      changed = {this.nameChangedHandler}/>
    </div>
  );
}

}

export default App;
