import React, { Component } from 'react';
import './Post.css';
import HomeHeader from '../Header/HomeHeader'
import Footer from '../Footer/Footer'
import axios from 'axios'
class HomePage extends Component {
constructor() {
super();
this.state = {
name: '',
age: '',
place: '',
gender: '',
error: '',
};
this.handlePassChange = this.handlePassChange.bind(this);
this.handleUserChange = this.handleUserChange.bind(this);
this.handleSubmit = this.handleSubmit.bind(this);
this.dismissError = this.dismissError.bind(this);
}
dismissError() {
this.setState({ error: '' });
}
handleSubmit(evt) {
evt.preventDefault();
if (!this.state.username) {
return this.setState({ error: 'Username is required' });
}
if (!this.state.password) {
return this.setState({ error: 'Password is required' });
}
return this.setState({ error: '' });
}
handleUserChange(evt) {
this.setState({
username: evt.target.value,
});
};
handlePassChange(evt) {
this.setState({
password: evt.target.value,
});
}

onSubmitPost = () => {
    console.log("Calling Ajax function.....")
    console.log(this.state.name)
    const post = {
      name: this.state.name,
      age:  this.state.age,
      place: this.state.place
    };
    axios.post('http://localhost:8000/auth/login', post, {
    method: 'POST',
    headers: {
      'Accept': 'application/json',
      'content-type': 'application/json',
    },
  })
        .then((responseJson) => {
          console.log("This is my response Json", responseJson.data)
          var token = responseJson.data.token;
         // var message = responseJson.message;
          var userId = responseJson.data.userId;
          var message = responseJson.data.message;
          console.log("token"+ token);
          console.log("userId"+userId);
          alert(message)
          if (message === "Login Sucessfull"){
            this.props.history.push('/Home', {token : token})
          }
          // if (message === 'User Not Found !' || message === ' Email or Password wrong!' || message === 'Register Please!') {
          //          } else  {
          //         (message === 'undefined')
          //          console.log("Homepage");
          //          console.log('token'+token);
          //        //  this.props.navigation.navigate('HomePage',{token: token,user:user});
          //     }

        })
        .catch((error) => {
          console.error(error);
        });
}
handleRegister = () => {
  this.props.history.push('/Signup')
}
onPost = () => {
  this.props.history.push('/Post')
}
onPostHome = () => {
    this.props.history.push('/Home')
  }
  onGet = () => {
    this.props.history.push('/Get')
  }

render() {

// NOTE: I use data-attributes for easier E2E testing
// but you don't need to target those (any css-selector will work)
return (
  <div >
  <HomeHeader clickhome={() => this.onPostHome()}
  clickget={() => this.onGet()}/>
<h4 className="Post">
   <h2>Post Data Form</h2> 
<label>Name :  </label>
<input 
type="text" 
data-test="name" 
value={this.state.name} 
onChange={this.handleUserChange} />
<br/><br/>
<label>Age :  </label>
<input 
type="text"
data-test="text" 
value={this.state.age} 
onChange={this.handlePassChange} />
<br/><br/>
<label>Place :  </label>
<input 
type="text"
data-test="text" 
value={this.state.place} 
onChange={this.handlePassChange} />
<br/><br/>
<label>Gender :  </label>
<input 
type="text"
data-test="text" 
value={this.state.gender} 
onChange={this.handlePassChange} />
<br/><br/>
<input 
type="submit" 
value="Submit" 
data-test="submit" 
onClick={this.onSubmitPost}/>
</h4>
<Footer/>
</div>
);
}
}
export default HomePage;
