import React, { Component } from 'react';
//import logo from './logo.svg';
import './App.css';
import Person from './Person/Person'
import person from './Person/Person';
//import person from './Person/Person';


class App extends Component {
  state = {
    persons : [
      { name : 'Rahul', place : 'Panvel' }, 
      { name : 'Hemchandra', place : 'Banglore' },
      { name : 'Shrisha', place : 'Mulund' }
    ],
    showPersons : false
  }

  switchNameHandler = (newName) => {
    console.log("The Button was clicked");
    this.setState({
      persons : [
        { name : 'Avinash', place : 'Panvel' }, 
        { name : 'Abc', place : 'Banglore' },
        { name : 'Xyz', place : 'Delhi' }
      ]})
  }

  nameChangedHandler = (abcd) => {
    console.log("printing events------->>>", abcd)
 this.setState({
      persons : [
        { name : 'Avinash', place : 'Panvel' }, 
        { name : 'Manish', place : 'Banglore' },
        { name : abcd.target.value, place : 'Delhi' }
      ]})
  }
  togglePersonsHandler = () => {
    const doesShow = this.state.showPersons;
    this.setState( { showPersons: !doesShow } );
    //this.setState( { showPersons: !doesShow } );
  }

  render() {
    const style = {
      backgroundColor : 'white',
      font: 'inherit',
      border: '1px solid blue',
      padding : '8px', 
      boxShadow: '0 2px 3px rgb(73, 10, 219)', 
      cursor: 'pointer'
    }
    let persons = null;
    if (this.state.showPersons === true){
      persons = (
        <div>
          {this.state.persons.map(person => {
         <Person name={this.state.persons.name} place={this.state.persons.place}/>
          })}

        <Person name={this.state.persons[1].name} place={this.state.persons[1].place}/>
        <Person name={this.state.persons[2].name} place={this.state.persons[2].place}
        changed = {this.nameChangedHandler}/>
        </div>
      )
    }
  return (
    <div className="App">
      <h1>Hi, I am a React App</h1>
      <p>This is really Working</p>
      <button 
      style={style}
      onClick={this.togglePersonsHandler} >Switch Button</button>
      {persons}
    </div>
  );
}

}

export default App;
