import React, { Component } from 'react';
//import logo from './logo.svg';
import './App.css';
import Person from './Person/Person'



class App extends Component {
  state = {
    persons : [
      { name : 'Rahul', place : 'Panvel' , id : '22'}, 
      { name : 'Test', place : 'Banglore', id : '11' },
      { name : 'Ram', place : 'Mulund', id : '90' }
    ],
    showPersons : false
  }

  // switchNameHandler = (newName) => {
  //   console.log("The Button was clicked");
  //   this.setState({
  //     persons : [
  //       { name : 'Avinash', place : 'Panvel' }, 
  //       { name : 'Abc', place : 'Banglore' },
  //       { name : 'Xyz', place : 'Delhi' }
  //     ]})
  // }

  deletePersonHandler = (personIndex) => {
    console.log("delete button clicked.......")
    const persons = this.state.persons;
    persons.splice(personIndex, 1); //this removes 1 element from array
    this.setState({persons : persons})  // setting old state to the new Person state
  }

  nameChangedHandler = (event, abcd) => {
    console.log("printing events------->>>", abcd)
   const personIndex = this.state.persons.findIndex(p => {
     return p.abcd === abcd
   })

   const person = {
     ...this.state.persons[personIndex]

   };

   person.name = event.target.value;

   const persons =  [...this.state.persons];
   person[personIndex] = person;

 this.setState({persons : persons});
  togglePersonsHandler = () => {
    console.log("printing events------->>>")
    const doesShow = this.state.showPersons;
    this.setState( { showPersons: !doesShow } );
    console.log("comming to line 49")
    //this.setState( { showPersons: !doesShow } );
  }

  render() 
{
    const style = {
      backgroundColor : 'white',
      font: 'inherit',
      border: '1px solid blue',
      padding : '8px', 
      boxShadow: '0 2px 3px rgb(73, 10, 219)', 
      cursor: 'pointer'
    }
     let persons = null;
    if (this.state.showPersons === true){
      persons = (
        <div>
          {this.state.persons.map((person, index) => {
         return<Person 
         click={() => this.deletePersonHandler(index)}
         name={person.name} 
         place={person.place}
         id={person.id}
        changed = {(event) => this.nameChangedHandler(event, person.id)} 
        />

          })}


        </div>
      )
    }
  return (
    <div className="App">
      <h1>Hi, I am a React App</h1>
      <p>This is really Working</p>
      <button 
      style={style}
      onClick={this.togglePersonsHandler} >Switch Button</button>
      {persons}
    </div>
  );
}
  }
}

export default App;
