import React, { Component } from 'react';
//import logo from './logo.svg';
import './App.css';
import Person from './Person/Person'


class App extends Component {
  state = {
    persons : [
      { name : 'Rahul', place : 'Panvel' }, 
      { name : 'Hemchandra', place : 'Banglore' },
      { name : 'Shrisha', place : 'Mulund' }
    ]
  }

  switchNameHandler = () => {
    console.log("The Button was clicked");
    this.setState({
      persons : [
        { name : 'Avinash', place : 'Panvel' }, 
        { name : 'Manish', place : 'Banglore' },
        { name : 'Varun', place : 'Delhi' }
      ]})
  }

  nameChangedHandler = (event) => {
    this.setState({
      persons : [
        { name : 'Avinash', place : 'Panvel' }, 
        { name : 'Manish', place : 'Banglore' },
        { name : 'Varun', place : 'Delhi' }
      ]})
  }
  render() {
  return (
    <div className="App">
      <h1>Hi, I am a React App</h1>
      <p>This is really Working</p>
      <button onClick={this.switchNameHandler}>Switch Button</button>
      <Person name={this.state.persons[0].name} place={this.state.persons[0].place}/>
      <Person name={this.state.persons[1].name} place={this.state.persons[1].place}/>
      <Person name={this.state.persons[2].name} place={this.state.persons[2].place}/>
    </div>
  );
}

}

export default App;
