import React from 'react';
import LeftSideBar from '../../Components/left-sidebar';
import DashboardNav from '../../Components/right-sidebar';
import { Link } from 'react-router-dom';
import RightSideBar from '../components/right-sidebar/rightSidebar';
import Moment from 'react-moment';
import 'moment-timezone';
import {
  getTodaysAppointment,
  newPatientCounts,
  getPendingAppointmentCount,
  getnextappointmentDetails,
  getDoctorPayment
} from '../services/dashboard-service';
import ProgressiveImage from 'react-progressive-image';
import DummyDoctor from '../assets/images/doctors/member1.png';
import userStore from '../common/user-store';

class Dashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      newPatientCount: '',
      todaysappointmentcount: '',
      pendingAppointmentCount: '',
      todayPaymentAmount: '',
      weekPaymentAmount: '',
      monthPaymentAmount: '',
      nextappointment: [],
      newPatientDetails: [],
      todasappointmentDetails: []
    };
  }
  componentDidMount() {
    let self = this;
    let lastSegment = userStore.get('_id');
    // let todaydateformat = moment().format('MMMM D, YYYY 00:00');
    let todaydateformat = moment().format('MM/DD/YYYY 00:00');
    let tomorrowdateformat = moment().format('MM/DD/YYYY 23:59');
    let todaydateformat1 = moment().format('MM/DD/YYYY 00:00');
