import React, { Component } from 'react';
import './Login.css';
import Header from '../Header/Header'
import Footer from '../Footer/Footer'
import axios from 'axios'
import Box from '@material-ui/core/Box';
import CircularProgress from '@material-ui/core/CircularProgress';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
class LoginPage extends Component {
constructor() {
super();
this.state = {
username: '',
password: '',
error: '',
};
this.handlePassChange = this.handlePassChange.bind(this);
this.handleUserChange = this.handleUserChange.bind(this);
this.handleSubmit = this.handleSubmit.bind(this);
this.dismissError = this.dismissError.bind(this);
}
dismissError() {
this.setState({ error: '' });
}
handleSubmit(evt) {
evt.preventDefault();
if (!this.state.username) {
return this.setState({ error: 'Username is required' });
}
if (!this.state.password) {
return this.setState({ error: 'Password is required' });
}
return this.setState({ error: '' });
}
handleUserChange(evt) {
this.setState({
username: evt.target.value,
});
};
handlePassChange(evt) {
this.setState({
password: evt.target.value,
});
}
handleRegister = () => {
  this.props.history.push('/Signup')
}
handleLogin = () => {
  console.log("Calling Ajax function.....")
  console.log(this.state.username)
  console.log(this.state.password)
  const post = {
    email: this.state.username,
    password:  this.state.password
  };
  axios.post('http://localhost:8000/auth/login', post, {
  method: 'POST',
  headers: {
    'Accept': 'application/json',
    'content-type': 'application/json',
  },
})
      .then((responseJson) => {
        console.log("This is my response Json", responseJson.data)
        var token = responseJson.data.token;
       // var message = responseJson.message;
        var userId = responseJson.data.userId;
        var message = responseJson.data.message;
        console.log("token"+ token);
        console.log("userId"+userId);
        alert(message)
        if (message === "Login Sucessfull"){
          this.props.history.push('/Home', {token : token})
        }
        // if (message === 'User Not Found !' || message === ' Email or Password wrong!' || message === 'Register Please!') {
        //          } else  {
        //         (message === 'undefined')
        //          console.log("Homepage");
        //          console.log('token'+token);
        //        //  this.props.navigation.navigate('HomePage',{token: token,user:user});
        //     }

      })
      .catch((error) => {
        console.error(error);
      });
}


render() {

// NOTE: I use data-attributes for easier E2E testing
// but you don't need to target those (any css-selector will work)
return (
  <div>
  <Header/>
<div className="Login">

<form onSubmit={this.handleSubmit}>
{
this.state.error &&
<h3 data-test="error" onClick={this.dismissError}>
<button onClick={this.dismissError}>âœ–</button>
{this.state.error}
</h3>
}
<TextField 
id="outlined-basic" 
label="Email Id" 
variant="outlined"  
value={this.state.username} 
onChange={this.handleUserChange} />
<br/><br/>
<TextField 
id="outlined-basic" 
label="Password" 
variant="outlined"
type="password" 
data-test="password" 
value={this.state.password} 
onChange={this.handlePassChange} />
<br/><br/>
<Button 
variant="contained" 
color="primary" 
component="span"
onClick={this.handleLogin}>Submit</Button>
 <CircularProgress disableShrink />
<h4>Not you signed up?<a href="" onClick={this.handleRegister}>Register here</a> </h4>
</form>
</div>
<Footer/>
</div>
);
}
}
export default LoginPage;
